      this.status, 
      this.code, 
      this.message, 
      this.totalResults, 
      this.articles,});

  ArticleManger.fromJson(dynamic json) {
    status = json['status'];
    code = json['code'];
    message = json['message'];
    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
